package interclasse;import java.util.Scanner;public class Principal {    public static void main(String[] args) {        Salas primeiros = Salas.PRIMEIROS;        Salas segundos = Salas.SEGUNDOS;        Salas terceiros = Salas.TERCEIROS;        Grupos grupos = new Grupos();        Scanner sc = new Scanner(System.in);        String a;        do {        System.out.println("\n\nDigite o ano\n1-Primeiro\n2-Segundo\n3-Terceiro" +                "\n4-Montar grupos e mostrar");        a = sc.next();            switch (a) {                case "1":                    primeiros.montarGruposParticipantes();                    primeiros.montadorGrupos();                    break;                case "2":                    segundos.montarGruposParticipantes();                    segundos.montadorGrupos();                    break;                case "3":                    terceiros.montarGruposParticipantes();                    terceiros.montadorGrupos();                    break;                case "4":                    grupos.montarGrupos(primeiros, segundos, terceiros);                    grupos.printGrupos();                    break;                case "5":                    grupos.selecionarGrupo();                    break;                case "0":                    break;                default:                    System.out.println("Digite apenas n√∫meros (0 a 4)");            }        }while(!a.equals("0"));    }}