package interclasse;import java.util.Scanner;public class Principal {    public static void main(String[] args) {        Salas primeiros = Salas.PRIMEIROS;        Salas segundos = Salas.SEGUNDOS;        Salas terceiros = Salas.TERCEIROS;        Scanner sc = new Scanner(System.in);        int a;        do {        System.out.println("\nDigite o ano\n1-Primeiro\n2-Segundo\n3-Terceiro");        a = sc.nextInt();            switch (a) {                case 1:                    primeiros.montarGruposParticipantes();                    break;                case 2:                    segundos.montarGruposParticipantes();                    break;                case 3:                    terceiros.montarGruposParticipantes();                    break;                case 0:                    break;            }        }while(a != 0);    }}