package interclasse;import java.util.Scanner;public class Principal {    public static void main(String[] args) {        Salas primeiros = Salas.PRIMEIROS;        Salas segundos = Salas.SEGUNDOS;        Salas terceiros = Salas.TERCEIROS;        Times time = new Times();        Scanner sc = new Scanner(System.in);        String a;        do {        System.out.println("\nDigite o ano\n1-Primeiro\n2-Segundo\n3-Terceiro" +                "\n4-Mostrar todos os grupos\n5-Insersao de pontos");        a = sc.next();            switch (a) {                case "1":                    primeiros.montarGruposParticipantes();                    primeiros.printGrupos();                    break;                case "2":                    segundos.montarGruposParticipantes();                    segundos.printGrupos();                    break;                case "3":                    terceiros.montarGruposParticipantes();                    terceiros.printGrupos();                    break;                case "4":                    primeiros.printGrupos();                    segundos.printGrupos();                    terceiros.printGrupos();                    break;                case "5":                    System.out.print("Escola um grupo (1 a 6)");                    int grupo = sc.nextInt();                    switch (grupo){                        case 1:                            System.out.print("Escola uma sala (1 a 3)");                            int sala = sc.nextInt() - 1;                            time.selecionarTime(primeiros.getGrupo1(), sala);                            System.out.print("Digite os pontos proprios");                            int pontosPro = sc.nextInt();                            System.out.print("Digite os pontos contra");                            int pontosCon = sc.nextInt();                            System.out.print("Digite os pontos da partida");                            int pontos = sc.nextInt();                            time.insercaoPontos(pontosPro, pontosCon, pontos);                            time.listar();                            break;                     }                    break;                case "0":                    break;                default:                    System.out.println("Digite apenas n√∫meros (0 a 4)");            }        }while(!a.equals("0"));    }}