package interclasse;import java.util.Scanner;public class Principal {    public static void main(String[] args) {        Salas primeiros = Salas.PRIMEIROS;        Salas segundos = Salas.SEGUNDOS;        Salas terceiros = Salas.TERCEIROS;        Grupos grupos = new Grupos();        Scanner sc = new Scanner(System.in);        String a;        String a2;        do {        System.out.println("\n\nMontar Grupos:\n1. Primeiros\n2. Segundos\n3. Terceiros" +                "\n\n4. Menu grupos\n0. Sair");        a = sc.next();            switch (a) {                case "1":                    primeiros.montarGruposParticipantes();                    primeiros.montadorGrupos();                    break;                case "2":                    segundos.montarGruposParticipantes();                    segundos.montadorGrupos();                    break;                case "3":                    terceiros.montarGruposParticipantes();                    terceiros.montadorGrupos();                    break;                case "4":                    do {                        System.out.println("\n\nDigite a opção desejada\n1. Mostrar Grupos\n2. Selecionar sala");                        a2 = sc.next();                        switch (a2) {                            case "1":                                grupos.montarGrupos(primeiros, segundos, terceiros);                                grupos.printGrupos();                                break;                            case "2":                                grupos.selecionarGrupo();                                break;                            case "0":                                break;                            default:                                System.out.println("Digite apenas números (0 a 4)");                        }                    }while(!a2.equals("0"));                    break;                case "0":                    break;                default:                    System.out.println("Digite apenas números (0 a 4)");            }        }while(!a.equals("0"));    }}